mapper.select:
select ns.share_id,g.buy_time, from np_comment c LEFT JOIN np_order_goods g on g.comment_id = c.comment_id LEFT JOIN np_share ns ON g.share_id = ns.share_id AND (ns.is_display = '1' OR ns.is_display = '2' ) where c.del_flag ='0' and
c.is_consult ='0'
c.is_consult ='0' and comment_type = #{commentType,jdbcType=CHAR}
c.is_consult = '1' and comment_content like CONCAT(CONCAT('%', #{paramString}),'%')
and c.consult_item = #{item,jdbcType=VARCHAR}
and g.share_id = ns.share_id
and c.goods_id=#{goodsId,jdbcType=BIGINT} and c.is_display ='1' order by publish_time desc
limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

select

from np_comment c
where c.comment_id = #{commentId,jdbcType=BIGINT}

mapper.update:
update np_comment
set del_flag = '1', del_time = SYSDATE()
where comment_id = #{parseLong,jdbcType=BIGINT}
