mapper.select:
select , enter.company_name from np_customer c left join np_customer_info i on i.customer_id=c.customer_id
left join qp_customer_rela_salesman reasala on reasala.customer_id = c.customer_id and reasala.del_flag='0' left join qp_salesman sala on reasala.salesman_id = sala.salesman_id and sala.del_flag='0' AND sala.is_enabled='0' left join qp_enterprise_certification_info enter on enter.customerId = c.customer_id and enter.check_status='1' AND enter.del_flag='0'
where c.del_flag = '0' and (c.is_seller = '0') order by c.login_time desc limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

select city.city_id,city.city_name,city.province_id from np_sys_city city INNER JOIN np_sys_district dir on city.city_id=dir.city_id where district_id not in
#{id,jdbcType=BIGINT}
GROUP BY dir.city_id

select d.district_id,d.district_name,d.city_id from np_sys_district d where 1=1 and d.district_id not in
#{id,jdbcType=BIGINT}
and d.del_flag = '0'

