mapper.select:
SELECT c.customer_id, c.customer_username, c.customer_nickname, ca.address_name, ca.address_moblie, COUNT(o.order_id) as ordernumber, sum(o.order_price) as ordermoney FROM np_customer c LEFT JOIN np_order o ON o.customer_id = c.customer_id LEFT JOIN np_customer_address ca ON ca.customer_id=c.customer_id where o.business_id='0' and(order_status = '3' or order_status = '1' )
AND o.pay_time >= #{startTime}
AND o.pay_time <= #{endTime}
GROUP BY c.customer_id ORDER BY
ordernumber desc
ordermoney desc
ordernumber asc
ordermoney asc
limit #{startRowNum},#{endRowNum}

select count(1) from (SELECT c.customer_id, c.customer_username, COUNT(o.order_id) as ordernumber, sum(o.order_price) as ordermoney FROM np_customer c LEFT JOIN np_order o ON o.customer_id = c.customer_id where o.business_id='0' and order_status IN ('1','3')
AND o.pay_time >= #{startTime}
AND o.pay_time <= #{endTime}
GROUP BY c.customer_id)a

select cuin.customer_id as customerId , customer_username , cuin.point_level_name as pointLevelName ,a.countOrder as countOrder,a.sumPrice as sumPrice,b.maxtime as maxtime from np_customer cu LEFT JOIN np_customer_info cuin on cu.customer_id=cuin.customer_id LEFT JOIN (select customer_id,count(1) countOrder ,sum(order_price) sumPrice from np_order o where o.order_status='3' GROUP BY o.customer_id ) a on a.customer_id=cu.customer_id LEFT JOIN (select customer_id ,max(pay_time) as maxtime FROM np_order group by customer_id) b on b.customer_id=cu.customer_id where cu.del_flag='0'
and point_level_id = #{pointLevelId}
and customer_username like CONCAT(CONCAT('%', #{customerNickname}),'%')
limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

select count(1) from np_customer cu LEFT JOIN np_customer_info cuin on cu.customer_id=cuin.customer_id where cu.del_flag='0'
and point_level_id = #{pointLevelId}
and customer_username like CONCAT(CONCAT('%', #{customerNickname}),'%')

select  
from np_customer c  where 1=1

    and c.customer_id = #{customerId}

