mapper.select:
select * FROM ( select
from np_customer_insideletter ci left JOIN np_cust_letter_rela cr on cr.letter_id = ci.letter_id and cr.customer_id = #{customerId,jdbcType=BIGINT} where ci.customer_id = #{customerId,jdbcType=BIGINT} AND ci.receive_customer_id is null ) t where t.is_del = '0' or t.is_del is null and (SELECT create_time FROM np_customer WHERE customer_id = #{customerId,jdbcType=BIGINT}) <= t.create_time
order by t.letter_id DESC limit #{startRowNum},#{endRowNum}

mapper.update:
update np_cust_letter_rela
set is_del='1'
where rela_id=#{relaId,jdbcType=BIGINT}

update np_cust_letter_rela
set is_del='1'
where customer_id=#{customerId,jdbcType=BIGINT} and letter_id=#{letterId,jdbcType=BIGINT}
