mapper.update:
update np_customer_follow set del_flag='1',modified_time=now()
where follow_id in 

	#{atteId}

mapper.select:
select a.*,(select customer_username from np_customer where customer_id=a.customer_id) as customer_username, (select info_email from np_customer_info where customer_id=a.customer_id) as info_email, (select info_mobile from np_customer_info where customer_id=a.customer_id) as info_mobile, (select goods_info_item_no from np_goods_info where goods_info_id=a.goods_id) as goods_info_item_no, (select goods_info_stock from np_goods_info where goods_info_id=a.goods_id) as goods_info_stock, (select goods_info_name from np_goods_info where goods_info_id=a.goods_id) as goods_info_name from np_customer_follow a where del_flag='0'
and goods_id=#{productId}
limit #{startRowNum},#{endRowNum}
