mapper.select:
select count(1) from np_goods np
where (( np.audit_status = '1' and np.is_third = '1' and np.goods_delflag='0') or
      (np.goods_id in (select distinct info.goods_id from np_goods_info info where info.audit_status = '1' and info.goods_info_delflag = '0')))

	and np.goods_name like CONCAT(CONCAT('%',
	#{searchBean.goodsName}),'%')


	and np.goods_no = #{searchBean.goodsNo}


	and np.cat_id = #{searchBean.goodsCateId}


	and np.brand_id = #{searchBean.goodsBrandId}


	and np.goods_added = #{searchBean.status}


	and np.goods_name like CONCAT(CONCAT('%',
	#{searchBean.searchText}),'%')


	and np.goods_no = #{searchBean.searchText}


	and goods_keywords like CONCAT(CONCAT('%',
	#{searchBean.searchText}),'%')


	and np.goods_belo_name like CONCAT(CONCAT('%',
	#{searchBean.thirdName}),'%')

select , (select n.cat_name from np_goods_category n where np.cat_id = n.cat_id) cat_name, (select b.brand_name from np_goods_brand b where np.brand_id = b.brand_id) brand_name, (select store.BUSS_DATE from np_third_store_info store where np.goods_belo = store.store_id) store_valid_time from np_goods np where (( np.audit_status = '1' and np.is_third = '1' and np.goods_delflag='0') or (np.goods_id in (select distinct info.goods_id from np_goods_info info where info.audit_status = '1' and info.goods_info_delflag = '0')))
and np.goods_name like CONCAT(CONCAT('%', #{searchBean.goodsName}),'%')
and np.goods_no = #{searchBean.goodsNo}
and np.cat_id = #{searchBean.goodsCateId}
and np.brand_id = #{searchBean.goodsBrandId}
and np.goods_added = #{searchBean.status}
and np.goods_name like CONCAT(CONCAT('%', #{searchBean.searchText}),'%')
and np.goods_no = #{searchBean.searchText}
and goods_keywords like CONCAT(CONCAT('%', #{searchBean.searchText}),'%')
and np.goods_belo_name like CONCAT(CONCAT('%', #{searchBean.thirdName}),'%')
order by np.goods_create_time desc limit #{startRowNum},#{endRowNum}
