select count(1) from np_goods_info i
    inner join np_goods g on g.goods_delflag='0'

        and g.is_third = #{isThird}


        and brand_id in

            #{brandId}



        and cat_id in

            #{catId}


    and i.goods_id = g.goods_id

        left join (select IFNULL(sum(ware_stock),0) as stockSum,product_id from np_product_ware where del_flag = '0' group by product_id) as sto
        on sto.product_id = i.goods_info_id

where i.goods_info_delflag='0' and i.goods_info_added='1'

        and i.is_third = #{isThird}


        and (g.goods_name like CONCAT(CONCAT('%',
        #{goodsName}),'%'))


        and (g.goods_no like CONCAT(CONCAT('%',
        #{goodstNo}),'%'))



        and (i.goods_info_item_no like CONCAT(CONCAT('%',
        #{productNo}),'%'))





            and i.goods_info_vip_price >= #{lowGoodsInfoPrice}



            and i.goods_info_prefer_price >= #{lowGoodsInfoPrice}





            and i.goods_info_vip_price  <=  #{highGoodsInfoPrice}



            and i.goods_info_prefer_price  <=  #{highGoodsInfoPrice}




        and i.show_mobile = '1'


        and i.third_id = #{thirdId}


        and (i.goods_info_name = #{goodsInfoName}
        or i.goods_info_name like CONCAT(CONCAT('%',#{goodsInfoName}),'%'))



            and sto.stockSum > 0


            and sto.stockSum = 0

select i.goods_info_id, i.goods_id, i.goods_info_item_no,i.goods_info_name,i.goods_info_subtitle, i.goods_info_added, i.goods_info_added_time,i.goods_info_unadded_time, i.goods_info_stock, i.goods_info_prefer_price, i.goods_info_vip_price, i.goods_info_cost_price, i.goods_info_market_price, i.goods_info_weight, i.goods_info_img_id, i.goods_info_create_name, i.goods_info_create_time, i.goods_info_modified_name, i.goods_info_modified_time, i.goods_info_del_name, i.goods_info_del_time, i.goods_info_delflag,i.third_id,i.third_name,i.is_third,i.show_list, cat.cat_name,brand.brand_name from np_goods_info i inner join np_goods g on g.goods_delflag='0'
and g.is_third = #{isThird}
and brand_id in
#{brandId}
and cat_id in
#{catId}
and i.goods_id = g.goods_id inner join np_goods_category cat on cat.cat_id=g.cat_id inner join np_goods_brand brand on brand.brand_id = g.brand_id
left join (select IFNULL(sum(ware_stock),0) as stockSum,product_id from np_product_ware where del_flag = '0' group by product_id) as sto on sto.product_id = i.goods_info_id
where i.goods_info_delflag='0' and i.goods_info_added='1' and i.show_list='1'
and i.is_third = #{isThird}
and (g.goods_name like CONCAT(CONCAT('%', #{goodsName}),'%'))
and (g.goods_no like CONCAT(CONCAT('%', #{goodstNo}),'%'))
and (i.goods_info_item_no like CONCAT(CONCAT('%', #{productNo}),'%'))
and i.third_id = #{thirdId}
and i.show_mobile = '1'
and i.goods_info_name like CONCAT(CONCAT('%',#{goodsInfoName}),'%')
and i.goods_info_vip_price >= #{lowGoodsInfoPrice}
and i.goods_info_prefer_price >= #{lowGoodsInfoPrice}
and i.goods_info_vip_price <= #{highGoodsInfoPrice}
and i.goods_info_prefer_price <= #{highGoodsInfoPrice}
and sto.stockSum > 0
and sto.stockSum = 0
order by i.goods_info_create_time limit #{startRowNum},#{endRowNum}

select i.goods_info_id, i.goods_id, i.goods_info_item_no,i.goods_info_name,i.goods_info_subtitle, i.goods_info_added, i.goods_info_added_time,i.goods_info_unadded_time, i.goods_info_stock, i.goods_info_prefer_price, i.goods_info_vip_price, i.goods_info_cost_price, i.goods_info_market_price, i.goods_info_weight, i.goods_info_img_id, i.goods_info_create_name, i.goods_info_create_time, i.goods_info_modified_name, i.goods_info_modified_time, i.goods_info_del_name, i.goods_info_del_time, i.goods_info_delflag,i.third_id,i.third_name,i.is_third,i.show_list, cat.cat_name,brand.brand_name from np_goods_info i left join (select image_in_name as goods_info_img_id,goods_info_id from np_goods_image ngi where ngi.goods_img_delflag='0' group by goods_info_id) im on im.goods_info_id = i.goods_info_id inner join (select goods_id,cat_id ,brand_id from np_goods where is_third='1' and goods_belo = #{goodsBelo}) g on i.goods_id = g.goods_id inner join np_goods_category cat on cat.cat_id=g.cat_id inner join np_goods_brand brand on brand.brand_id = g.brand_id where i.goods_info_delflag='0' and i.goods_info_added='1' and i.is_third='1'
and i.goods_info_name like CONCAT(CONCAT('%',#{goodsInfoName}),'%')
and i.goods_info_item_no = #{goodsInfoNo}
and i.show_mobile = '1'
order by i.goods_info_create_time limit #{startRowNum},#{endRowNum}

