mapper.select:
SELECT gi.goods_id, gi.goods_info_id, gi.goods_info_name, gi.goods_info_item_no, gi.goods_info_img_id, sum(og.goods_info_num) AS goodscount, gi.goods_info_prefer_price, gi.goods_info_vip_price FROM np_goods g LEFT JOIN np_goods_info gi ON gi.goods_id = g.goods_id LEFT JOIN np_order_goods og ON og.goods_info_id = gi.goods_info_id LEFT JOIN np_order o ON o.order_id = og.order_id WHERE g.is_third = '0' AND o.business_id = '0' AND o.order_status IN ('1', '2', '3', '9')
AND o.pay_time >= #{startTime}
AND o.pay_time <= #{endTime}
GROUP BY gi.goods_info_id ORDER BY goodscount DESC limit #{startRowNum},#{endRowNum}

select count(1) from (SELECT count(1) FROM np_goods g LEFT JOIN np_goods_info gi ON gi.goods_id = g.goods_id LEFT JOIN np_order_goods og ON og.goods_info_id = gi.goods_info_id LEFT JOIN np_order o ON o.order_id = og.order_id WHERE g.is_third = '0' AND o.business_id = '0' AND o.order_status IN ('1', '2', '3')
AND o.pay_time between #{startTime} and #{endTime}
GROUP BY gi.goods_info_id)a

