mapper.select:
select count(1) from np_marketing m ,np_marketing_codex mc,np_codex codex where m.marketing_id = mc.marketing_id and mc.codex_id = codex.codex_id AND mc.flag='0' and m.flag = '0'
and m.marketing_type = #{marketingType,jdbcType=CHAR}
and codex.codex_id in(1,9)
and business_id = #{businessId}
and business_id is null

select mgroup.preferential_name, m.marketing_id, m.marketing_name, m.marketing_des, m.marketing_type, m.marketing_begin, m.marketing_end,m.is_repeat, m.create_time, m.mod_time, m.flag,m.is_all,codex.codex_name,codex.codex_id,codex.codex_type from np_marketing m ,np_marketing_codex mc,np_codex codex,np_marketing_group mgroup where m.marketing_id = mc.marketing_id and mc.codex_id = codex.codex_id and mc.flag='0' and m.flag = '0' and codex.codex_status=mgroup.group_id
and m.marketing_type = #{marketingType,jdbcType=CHAR}
and codex.codex_id in(1,9)
and codex.codex_id in(15)
and codex.codex_id in(12)
and codex.codex_id in(5,8)
and codex.codex_id in(13,14)
and business_id = #{businessId}
and business_id is null
order by m.mod_time desc limit #{start},#{number}
