select count(order_id) as daycount, DATE_FORMAT (pay_time, '%Y-%m-%d') as pay_time from np_order where business_id='0'
and pay_time between #{startTime} and #{endTime}
and order_status = '3' group by DATE_FORMAT(pay_time,'%Y-%m-%d')

select sum(order_price) as daymoney,DATE_FORMAT (pay_time, '%Y-%m-%d') as pay_time from np_order where business_id='0' and order_status = '3'
and pay_time between #{startTime} and #{endTime}
group by DATE_FORMAT(pay_time,'%Y-%m-%d')

