mapper.update:
	update np_order_goods


      comment_id = #{commentId,jdbcType=BIGINT},


      evaluate_flag = #{evaluateFlag,jdbcType=BIGINT},


  where 1=1

        and  order_id = #{orderId,jdbcType=BIGINT}


        and  goods_info_id  = #{goodsId,jdbcType=BIGINT}


        and  order_goods_id  = #{orderGoodsId,jdbcType=BIGINT}

mapper.select:
SELECT count(1) from np_order orders INNER JOIN np_order_goods goods on orders.order_id=goods.order_id where orders.del_flag='0' and goods.del_flag='0' and goods.order_goods_id= #{orderGoodsId,jdbcType=BIGINT} and orders.customer_id=#{customerId,jdbcType=BIGINT}
and goods.evaluate_flag='0'
and goods.share_flag='0'
