mapper.select:
select , IFNULL(w.ware_stock,0) AS ware_stock, IFNULL(w.ware_price,i.goods_info_prefer_price) AS ware_price, IFNULL(w.ware_vip_price,i.goods_info_vip_price) AS ware_vip_price from np_goods_info i inner join (select goods_id,cat_id,brand_id from np_goods) j on j.goods_id = i.goods_id left join (select sum(goods_info_num) as count, np_order_goods.goods_info_id from np_order_goods where order_id in (select order_id from np_order where order_status='3') group by goods_info_id) o on o.goods_info_id=i.goods_info_id left join (select ware_stock,product_id,ware_price,ware_vip_price from np_product_ware where ware_id in (select ware_id from np_ware_city where city_id=#{distinctId} AND del_flag='0')) w on w.product_id = i.goods_info_id left join (select count(1) as attecount,goods_id from np_customer_follow group by goods_id) a on a.goods_id=i.goods_info_id
INNER join (select s.goods_id from (select h.goods_id,count(h.goods_id) as c from (select x.goods_id from np_goods_rele_expandparam as x where 1=1 and
x.expangparam_value_id =#{param}
and x.rele_expandparam_delflag='0') as h GROUP BY H.goods_id HAVING c >= #{paramLengh} ) s ) ex on ex.goods_id = i.goods_id
INNER join (select s.goods_info_id from (select h.goods_info_id,count(h.goods_info_id) as c from (select x.goods_info_id from np_goods_info_rele_spec_detail as x where 1=1 and
x.spec_detail_id = #{spec}
) as h GROUP BY h.goods_info_id HAVING c >= #{specsLengh} ) s ) sp on sp.goods_info_id = i.goods_info_id
where i.goods_info_delflag='0' and i.goods_info_added='1'
and j.brand_id=#{brandId}
and ex.goods_id is not null
and sp.goods_info_id is not null
and i.goods_info_id in
#{productId}
and i.goods_info_stock>0
and i.goods_info_vip_price > #{searchBean.priceMin}
and i.goods_info_prefer_price > #{searchBean.priceMin}
and #{searchBean.priceMax} > i.goods_info_vip_price
and #{searchBean.priceMax} > i.goods_info_prefer_price
order by i.goods_info_vip_price
order by i.goods_info_prefer_price
order by i.goods_info_vip_price desc
order by i.goods_info_prefer_price desc
order by o.count desc
order by o.count
order by i.goods_info_create_time
order by i.goods_info_create_time desc
order by a.attecount
order by a.attecount desc
order by field(i.goods_info_id,
#{id}
)
limit #{startRowNum},#{endRowNum}

		select COUNT(1) from np_goods_info where 
		goods_info_delflag='0' and goods_info_added='1' 


			and goods_info_id in

				#{productId}




			and goods_info_stock>0





                and goods_info_vip_price > #{searchBean.priceMin}



                and goods_info_prefer_price > #{searchBean.priceMin}





                and #{searchBean.priceMax} > goods_info_vip_price



                and #{searchBean.priceMax} > goods_info_prefer_price

select distinct from np_goods g,np_goods_info i where g.goods_id=i.goods_id and g.goods_delflag='0' and g.goods_added='1' and cat_id in
#{catId}
limit 0,4

select COUNT(1) from np_goods_info i inner join (select goods_id,cat_id,brand_id from np_goods) j on j.goods_id = i.goods_id
inner join (select product_id,ware_stock from np_product_ware where ware_id = (select ware_id from np_ware_city where city_id=#{distinctId} limit 0,1)) as w on w.product_id = i.goods_info_id
inner join (select s.goods_id from (select h.goods_id,count(h.goods_id) as c from (select x.goods_id from np_goods_rele_expandparam as x where 1=1 and
x.expangparam_value_id = #{param}
and x.rele_expandparam_delflag='0' ) as h GROUP BY h.goods_id HAVING c >= #{paramLengh} ) s ) as ex on ex.goods_id = i.goods_id
left join (select s.goods_info_id from (select h.goods_info_id,count(h.goods_info_id) as c from (select x.goods_info_id from np_goods_info_rele_spec_detail as x where 1=1 and
x.spec_detail_id = #{spec}
) as h GROUP BY h.goods_info_id HAVING c >= #{specsLengh} ) s ) as sp on sp.goods_info_id = i.goods_info_id
where j.goods_id = i.goods_id and goods_info_delflag='0' and goods_info_added='1' and show_list='1' and (audit_status='0' or audit_status='3') and
j.cat_id =#{catId}
and j.brand_id=#{brandId}
and ex.goods_id is not null
and sp.goods_info_id is not null
and w.ware_stock>0
and i.goods_info_vip_price > #{searchBean.priceMin}
and i.goods_info_prefer_price > #{searchBean.priceMin}
and #{searchBean.priceMax} > i.goods_info_vip_price
and #{searchBean.priceMax} > i.goods_info_prefer_price

		select count(i.goods_info_id) from np_goods_info i
        inner join (select goods_id,cat_id,brand_id from np_goods) j on j.goods_id = i.goods_id
		left join (select sum(goods_info_num) as count, np_order_goods.goods_info_id from np_order_goods 
					where order_id in (select order_id from np_order where order_status='3') group by goods_info_id) o 
					on o.goods_info_id=i.goods_info_id
		left join (select count(*) as attecount,goods_id from np_customer_follow group by goods_id) a on a.goods_id=i.goods_info_id

		right join np_goods ng
		on i.goods_id=ng.goods_id and ng.goods_delflag='0'


            left join (select s.goods_id from (select h.goods_id,count(h.goods_id) as c
            from (select x.goods_id from np_goods_rele_expandparam as x
            where 1=1 and

                x.expangparam_value_id =#{param}

            and x.rele_expandparam_delflag='0') as h GROUP BY H.goods_id HAVING c >= #{paramLengh} ) s ) ex
            on ex.goods_id = i.goods_id



            left join  (select s.goods_info_id from (select h.goods_info_id,count(h.goods_info_id) as c
            from (select x.goods_info_id from np_goods_info_rele_spec_detail as x
            where 1=1 and

                x.spec_detail_id = #{spec}

            ) as h GROUP BY h.goods_info_id HAVING c >= #{specsLengh} ) s ) sp
            on sp.goods_info_id = i.goods_info_id

		where 
		i.goods_info_delflag='0' and i.goods_info_added='1'


            and j.brand_id=#{brandId}



            and ex.goods_id is not null



            and sp.goods_info_id is not null



			and i.goods_info_id in

				#{productId}




		    and ng.third_cate_id in

		    	#{tCateId}




		    and i.third_id = #{thirdId}





                and i.goods_info_vip_price > #{searchBean.startPrice}



                and i.goods_info_prefer_price > #{searchBean.startPrice}





                and i.goods_info_vip_price < #{searchBean.endPrice}



                and i.goods_info_prefer_price < #{searchBean.endPrice}




			and i.goods_info_stock>0

select ,i.goods_info_stock ware_stock from np_goods_info i left join (select sum(goods_info_num) as count, np_order_goods.goods_info_id from np_order_goods where order_id in (select order_id from np_order where order_status='3') group by goods_info_id) o on o.goods_info_id=i.goods_info_id left join (select count(1) as attecount,goods_id from np_customer_follow group by goods_id) a on a.goods_id=i.goods_info_id
right join np_goods ng on i.goods_id=ng.goods_id and ng.goods_delflag='0' where i.goods_info_delflag='0' and i.goods_info_added='1'
and i.goods_info_id in
#{productId}
and ng.third_cate_id in
#{tCateId}
and i.third_id=#{thirdId}
and i.goods_info_vip_price > #{searchBean.startPrice}
and i.goods_info_prefer_price > #{searchBean.startPrice}
and i.goods_info_vip_price < #{searchBean.endPrice}
and i.goods_info_prefer_price < #{searchBean.endPrice}
and i.goods_info_stock>0
order by i.goods_info_vip_price
order by i.goods_info_prefer_price
order by i.goods_info_vip_price desc
order by i.goods_info_prefer_price desc
order by o.count desc
order by o.count
order by i.goods_info_create_time
order by i.goods_info_create_time desc
order by a.attecount
order by a.attecount desc
limit #{startRowNum},#{endRowNum}
