mapper.update:
update np_sys_pay
set pay_default = '0'
where pay_id != #{payId,jdbcType=BIGINT}

mapper.select:
select from np_sys_pay where del_flag='0'
and pay_name like CONCAT(CONCAT('%', #{searchText},'%'))
limit #{startRowNum},#{endRowNum}

select from np_sys_pay where del_flag='0'
and pay_name like CONCAT(CONCAT('%', #{searchText},'%'))
and pay_type='3' limit #{startRowNum},#{endRowNum}
