mapper.update:
update np_sys_stockwarning 
set sw_value=#{swValue,jdbcType=BIGINT}
where sw_id=#{swId,jdbcType=BIGINT}

mapper.select:
select goods_info_name as goodname,goods_info_item_no as number,ware_id as wareid,ware_name as warename,ware_stock as stock, pp.goods_info_prefer_price as preferprice,pp.goods_info_img_id as imgid, pp.third_id as third, pp.goods_info_id as id,g.goods_belo_name as storename ,id as stockid,pp.is_third as is_third from (select infos.third_id, infos.goods_info_id,infos.is_third ,infos.goods_info_name,infos.goods_id,infos.goods_info_prefer_price,infos.goods_info_img_id, wares.id,wares.ware_id,wares.ware_name,infos.goods_info_item_no,wares.ware_stock from np_goods_info infos, (select product_id,ware.ware_id,house.ware_name,ware.ware_stock ,ware.id from np_product_ware ware,np_warehouse house where ware.ware_id=house.ware_id and ware.del_flag='0' and house.del_flag='0') wares where infos.goods_info_id = wares.product_id and infos.goods_info_delflag='0' and goods_info_added='1' ) as pp left join np_goods g on g.goods_id = pp.goods_id where (select sw_value from np_sys_stockwarning)>ware_stock
and goods_info_id=#{id,jdbcType=BIGINT}
and pp.goods_info_name like CONCAT(CONCAT('%',#{goodsName,jdbcType=VARCHAR},'%'))
and pp.goods_info_item_no like CONCAT(CONCAT('%',#{goodsNo,jdbcType=VARCHAR},'%'))
and pp.is_third = #{isThird,jdbcType=CHAR}
GROUP BY number limit #{startRowNum},#{endRowNum}

  SELECT spc.spec_name as specname,re.spec_value_remark as specvalue FROM 
np_goods_info_rele_spec_detail re LEFT JOIN np_goods_spec spc on spc.spec_id=re.spec_id
where re.goods_info_id=#{id,jdbcType=BIGINT}

select goods_info_name as goodname,goods_info_item_no as number,ware_id as wareid,ware_name as warename,ware_stock as stock, pp.third_id as third, goods_info_id as id,g.goods_belo_name as storename ,id as stockid from (select infos.third_id,infos.is_third, goods_info_id ,infos.goods_info_name,infos.goods_id,wares.id, wares.ware_id,wares.ware_name,infos.goods_info_item_no,wares.ware_stock from np_goods_info infos, ( select product_id,ware.ware_id,house.ware_name,ware.ware_stock ,ware.id from np_product_ware ware,np_warehouse house where ware.ware_id=house.ware_id and ware.del_flag='0' and house.del_flag='0') wares where infos.goods_info_id = wares.product_id and infos.goods_info_delflag='0' ) as pp left join np_goods g on g.goods_id = pp.goods_id where (select sw_value from np_sys_stockwarning)>ware_stock
and goods_info_id=#{id,jdbcType=BIGINT}
limit #{startRowNum},#{endRowNum}


 select count(th.id)
			 from (select goods_info_name as goodname,goods_info_item_no as number,ware_id as wareid,ware_name as warename,ware_stock  as stock,
 pp.third_id as third, pp.goods_info_id as id,g.goods_belo_name as storename ,id as stockid,pp.is_third as is_third from 
(select infos.third_id,infos.is_third, infos.goods_info_id ,infos.goods_info_name,infos.goods_id,wares.id,
wares.ware_id,wares.ware_name,infos.goods_info_item_no,wares.ware_stock 
from np_goods_info infos,
(select product_id,ware.ware_id,house.ware_name,ware.ware_stock ,ware.id
from np_product_ware ware,np_warehouse house where ware.ware_id=house.ware_id and ware.del_flag='0' and house.del_flag='0')
 wares
where infos.goods_info_id = wares.product_id
and infos.goods_info_delflag='0' and goods_info_added='1'
) as pp
left join np_goods g
on g.goods_id = pp.goods_id
where (select sw_value from np_sys_stockwarning)>ware_stock

   and goods_info_id=#{id,jdbcType=BIGINT}


   and goods_info_name like CONCAT(CONCAT('%',#{goodsName,jdbcType=VARCHAR},'%'))



   and goods_info_item_no like CONCAT(CONCAT('%',#{goodsNo,jdbcType=VARCHAR},'%'))



   and pp.is_third  = #{isThird,jdbcType=CHAR}

   GROUP BY number
) th

 select count(th.id)
			 from (select goods_info_name as goodname,goods_info_item_no as number,ware_id as wareid,ware_name as warename,ware_stock  as stock,
 pp.third_id as third, pp.goods_info_id as id,g.goods_belo_name as storename ,id as stockid from 
(select infos.third_id, infos.goods_info_id ,infos.goods_info_name,infos.goods_id,wares.id,
wares.ware_id,wares.ware_name,infos.goods_info_item_no,wares.ware_stock 
from np_goods_info infos,
(select product_id,ware.ware_id,house.ware_name,ware.ware_stock ,ware.id
from np_product_ware ware,np_warehouse house where ware.ware_id=house.ware_id and ware.del_flag='0' and house.del_flag='0')
 wares
where infos.goods_info_id = wares.product_id
and infos.goods_info_delflag='0'
) as pp
left join np_goods g
on g.goods_id = pp.goods_id
where (select sw_value from np_sys_stockwarning)>ware_stock

   and goods_info_id=#{id,jdbcType=BIGINT}

) th

select goods_info_name as goodname,goods_info_item_no as number,ware_id as wareid,ware_name as warename,ware_stock as stock, pp.third_id as third, pp.goods_info_id as id,g.goods_belo_name as storename ,id as stockid from (select infos.third_id, infos.goods_info_id ,infos.goods_info_name,infos.goods_id, wares.id,wares.ware_id,wares.ware_name,infos.goods_info_item_no,wares.ware_stock from np_goods_info infos, (select product_id,ware.ware_id,house.ware_name,ware.ware_stock ,ware.id from np_product_ware ware,np_warehouse house where ware.ware_id=house.ware_id and ware.del_flag='0') wares where infos.goods_info_id = wares.product_id and infos.goods_info_delflag='0' ) as pp left join np_goods g on g.goods_id = pp.goods_id where (select sw_value from np_sys_stockwarning)>ware_stock
and goods_info_name like CONCAT('%',#{searchText},'%')
and goods_info_item_no =#{searchText}
and goods_info_name like CONCAT('%',#{goodsName},'%')
and goods_info_item_no like CONCAT('%',#{goodsNo},'%')
GROUP BY number limit #{startRowNum},#{endRowNum}
